const React = require('react');

const Carousel = () => {
    const [categories, setCategories] = React.useState([]);
    const [categoryID, setCategoryID] = React.useState(null);
    const addClass = newClass => {
        setDynamicClasses([...dynamicClasses, newClass])
    }
    //get
    const getCategories = async () => {
        try {
            const response = await fetch("http://localhost:9000/categories");
            const jsonData = await response.json();

            setCategories(jsonData);
        } catch (err) {
            console.error(err.message);
        }
    }

    const selectCategory = (catID) => {
        setCategoryID(catID);
        console.log(categoryID)
    }
    React.useEffect(() => {
        getCategories().then(r => null);
    }, []);

    return (
        <div className="container">
            <div className="row">
                {categories.map(category => (
                    <div className="col" key={category.id}>
                        <button className={`btn btn-dark category-btn ${category.id === categoryID ? "active" : ""}`} onClick={() => selectCategory(category.id)}>{category.category}</button>
                    </div>
                ))}
            </div>
        </div>
    )
};

export default Carousel;
