const React = require('react');

const Carousel = () => {
    const [categories, setCategories] = React.useState([]);
    const [photos, setPhotos] = React.useState([]);
    const [categoryID, setCategoryID] = React.useState([]);
    //get categories
    const getCategories = async () => {
        try {
            const response = await fetch("http://localhost:9000/categories");
            const jsonData = await response.json();
            setCategories(jsonData);
        } catch (err) {
            console.error(err.message);
        }
    }
    const selectCategory = (catID) => {
        document.getElementById(`btn-${catID}`).classList.toggle('active');
        const allCategory = document.getElementsByClassName('.active').classList;
        allCategory.forEach(category => {
            showGallery(category);
        });
        // if(document.getElementById(`btn-${catID}`).classList.contains('active')) {
        //     setCategoryID(catID);
        //     const newCategory = (categoryID.length == 0) ? [catID] : [catID,categoryID];
        //     showGallery(newCategory);
        // }
    }
    // show gallery images in carousel
    const showGallery = async (catID) => {
        try {
            const response = await fetch('http://localhost:9000/photos', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({id : catID})
            }).then(response => response.json())
                .then((response) => { setPhotos(response)});
        } catch (err) {
            console.error(err.message);
        }
    }
    React.useEffect(() => {
        getCategories().then(r => null);
    }, []);

    return (
        <div className="container">
            <div className="row">
                {categories.map(category => (
                    <div className="col" key={category.id}>
                        <button data-id={`${category.id}`} id={`btn-${category.id}`} className={`btn btn-dark category-btn`} onClick={() => selectCategory(category.id)}>{category.category}</button>
                    </div>
                ))}
                <div id="carouselExampleDark" class="carousel carousel-dark slide" data-bs-ride="carousel">
                    { photos && photos.length ? (
                        <>
                            <div className="carousel-inner">
                                {
                                    photos.map((photo, index) => {
                                        return (
                                            <div className={`carousel-item ${index === 0 ? "active" : ""}`} key={photo.id}>
                                                <img src={photo.photo_url} className="carousel-image" />
                                            </div>
                                        )
                                    })
                                }
                            </div>
                            <button className="carousel-control-prev" type="button" data-bs-target="#carouselExampleDark"
                                    data-bs-slide="prev">
                                <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                                <span className="visually-hidden">Previous</span>
                            </button>
                            <button className="carousel-control-next" type="button" data-bs-target="#carouselExampleDark" data-bs-slide="next">
                                <span className="carousel-control-next-icon" aria-hidden="true"></span>
                                <span className="visually-hidden">Next</span>
                            </button>
                        </>
                    ): ""}
                </div>
            </div>
        </div>
    )
};

export default Carousel;
