const React = require('react');

const Carousel = () => {
    const [categories, setCategories] = React.useState([]);
    const [photos, setPhotos] = React.useState([]);
    const [categoryID, setCategoryID] = React.useState(false);
    //get
    const getCategories = async () => {
        try {
            const response = await fetch("http://localhost:9000/categories");
            const jsonData = await response.json();

            setCategories(jsonData);
        } catch (err) {
            console.error(err.message);
        }
    }

    const showGallery = async (catID) => {
        try {
            const response = await fetch("http://localhost:9000/categories/"+catID);
            const jsonData = await response.json();

            setPhotos(jsonData);
        } catch (err) {
            console.error(err.message);
        }
    }

    const selectCategory = (catID) => {
        setCategoryID(!categoryID);
        showGallery(catID);
    }
    React.useEffect(() => {
        getCategories().then(r => null);
    }, []);

    return (
        <div className="container">
            <div className="row">
                {categories.map(category => (
                    <div className="col" key={category.id}>
                        <button className={`btn btn-dark category-btn ${categoryID ? "active" : ""}`} onClick={() => selectCategory(category.id)}>{category.category}</button>
                        <div id="carouselExampleFade" className="carousel slide carousel-fade" data-bs-ride="carousel">
                            <div className="carousel-inner">
                                <div className="carousel-item active">
                                    <img src="https://founded.media/hiring/photos/sharks/11261840124_dc9ac72bbe_b.jpg" className="d-block w-100" alt="..." />
                                </div>
                                <div className="carousel-item">
                                    <img src="https://founded.media/hiring/photos/sharks/11261840124_dc9ac72bbe_b.jpg" className="d-block w-100" alt="..." />
                                </div>
                                <div className="carousel-item">
                                    <img src="https://founded.media/hiring/photos/sharks/11261840124_dc9ac72bbe_b.jpg" className="d-block w-100" alt="..." />
                                </div>
                            </div>
                            <button className="carousel-control-prev" type="button"
                                    data-bs-target="#carouselExampleFade" data-bs-slide="prev">
                                <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                                <span className="visually-hidden">Previous</span>
                            </button>
                            <button className="carousel-control-next" type="button"
                                    data-bs-target="#carouselExampleFade" data-bs-slide="next">
                                <span className="carousel-control-next-icon" aria-hidden="true"></span>
                                <span className="visually-hidden">Next</span>
                            </button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    )
};

export default Carousel;
