import useToken from '../useToken';
const React = require('react');

const Admin = () => {
    const { token, setToken } = useToken();
    const [showPhotos, setPhotoDetails] = React.useState([]);
    const [message, setMessage] = React.useState(null);
    const showAllRecords = async () => {
        try {
            const response = await fetch('http://localhost:9000/show-details', {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                }}).then(response => response.json())
                .then((response) => { setPhotoDetails(response)}
        );
        } catch (err) {
            console.error(err.message);
        }
    }

    const deleteRecord = async (catID) => {
        try {
            const response = await fetch(`http://localhost:9000/categories/delete/${catID}`);
            const jsonData = await response.json();

            setMessage(jsonData);
        } catch (err) {
            console.error(err.message);
        }
    }

    const addNewRecord = async () => {
        try {
            const response = await fetch('http://localhost:9000/categories/create', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                }}).then(response => response.json())
                .then((response) => { setMessage(response)});
        } catch (err) {
            console.error(err.message);
        }
    }

    React.useEffect(() => {
        showAllRecords().then(r => null);
    }, []);
    return (
        <>
            <div className="container-lg">
                <div className="message">{message}</div>
                <div className="table-responsive">
                    <div className="table-wrapper">
                        <div className="table-title">
                            <div className="row">
                                <div className="col-sm-8"><h2>Category <b>Details</b></h2></div>
                                <div className="col-sm-4">
                                    <button type="button" className="btn btn-info add-new" onClick={() => addNewRecord()}>Add New
                                    </button>
                                </div>
                            </div>
                        </div>
                        <table className="table table-bordered">
                            <thead>
                            <tr>
                                <th>Category</th>
                                <th>Photo URL</th>
                                <th>Actions</th>
                            </tr>
                            </thead>
                            <tbody>
                            {showPhotos.map(photos => (
                                <tr>
                                    <td>{photos.category}</td>
                                    <td><img src={photos.photo_url} className="table-images" /></td>
                                    <td>
                                        <a className="edit" title="Edit" data-toggle="tooltip">Edit</a>
                                        <a className="delete" title="Delete" data-toggle="tooltip" onClick={() => deleteRecord(photos.category_id)}>Delete</a>
                                    </td>
                                </tr>
                            ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </>
    );
};

export default Admin;
Admin.propTypes = {
    token: PropTypes.func.isRequired
};
