import useToken from '../useToken';
import { useNavigate } from 'react-router-dom';
const React = require('react');

const Admin = () => {
    const { token, setToken } = useToken();
    const [showPhotos, setPhotoDetails] = React.useState([]);
    const [message, setMessage] = React.useState(null);
    const navigate = useNavigate();
    const showAllRecords = async () => {
        try {
            const response = await fetch('http://localhost:9000/show-details', {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                }}).then(response => response.json())
                .then((response) => { setPhotoDetails(response)}
        );
        } catch (err) {
            console.error(err.message);
        }
    }

    const deleteRecord = async (photID) => {
        try {
            const response = await fetch(`http://localhost:9000/categories/delete/${photID}`, {
                method: 'DELETE',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                }}).then(response => response.json())
                .then(response => updateTable(response, photID))
        } catch (err) {
            console.error(err.message);
        }
    }

    const updateTable = (response, photID) => {
        setMessage(response);
        const allCategory = Array.from(
            document.querySelectorAll('table tr')
        );
        allCategory.forEach(element => {
            element.getAttribute('data-id').remove();
        });
        document.getElementsByTagName('table tr').getAttribute(`data-id='${photID}`).remove();
    }

    const addNewRecord = async () => {
        try {
            const response = await fetch('http://localhost:9000/categories/create', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                }}).then(response => response.json())
                .then((response) => { setMessage(response)});
        } catch (err) {
            console.error(err.message);
        }
    }
    const logout = () => {
        localStorage.setItem('token','');
        navigate('/login');
    }
    React.useEffect(() => {
        showAllRecords().then(r => null);
        console.log(showPhotos);
    }, []);
    return (
        <>
            <div className="container-lg">
                <div className="message">{message}</div>
                <a href="#" onClick={() => logout()}>Logout</a>
                <div className="table-responsive">
                    <div className="table-wrapper">
                        <div className="table-title">
                            <div className="row">
                                <div className="col-sm-8"><h2>Category <b>Details</b></h2></div>
                                <div className="col-sm-4">
                                    <button type="button" className="btn btn-info add-new" onClick={() => addNewRecord()}>Add New
                                    </button>
                                </div>
                            </div>
                        </div>
                        <table className="table table-bordered">
                            <thead>
                            <tr>
                                <th>Category</th>
                                <th>Photo URL</th>
                                <th>Actions</th>
                            </tr>
                            </thead>
                            <tbody>
                            {token && showPhotos.map(photos => (
                                <tr data-id={photos.id}>
                                    <td>{photos.category}</td>
                                    <td><img src={photos.photo_url} className="table-images" /></td>
                                    <td>
                                        <a className="edit" title="Edit" >Edit</a>
                                        <a className="delete" title="Delete" onClick={() => deleteRecord(photos.id)}>Delete</a>
                                    </td>
                                </tr>
                            ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </>
    );
};
export default Admin;
